INSERT INTO `memo` (`id`, `dep`, `field`, `user_id`, `memo_id`, `memo_num_dep`, `memo_date`, `title`, `paragraph1`, `paragraph2`, `paragraph3`, `sign`, `time_user`, `sign_officer`, `time_officer`, `sign_officer1`, `time_officer1`, `name`, `position`, `status`, `status_edit`, `comment1`, `comment2`, `sign_depart`, `depart_opinion`, `depart_opinion_time`, `memo_num_officer1`, `comment3`, `officer1_opinion`, `comment4`, `sign_head_officer`, `head_officer_opinion`, `head_officer_opinion_time`, `sign_admin`, `admin_opinion`, `admin_opinion_time`, `comment5`) VALUES
(1, 'ภาควิชามนุษยศาสตร์', 'สาขาวิชาภาษาอังกฤษ', '15', '0001', 'ภม.012/64', '2 มี.ค. 2565', 'ขออนุญาตดำเนินโครงการย่อย', 'ตามที่มหาวิทยาลัยราชภัฏเลยได้อนุมัติงบประมาณแผนงานโครงการยกระดับเศรษฐกิจและสังคมรายตำบลแบบบูรณาการ (มหาวิทยาลัยสู่ตำบล สร้างรากแก้วให้ประเทศ : U2T) มหาวิทยาลัยราชภัฏเลย โครงการย่อย “โครงการธนาคารขยะชุมชน สร้างความมั่นคงให้ครอบครัว พัฒนาชุมชนอย่างยั่งยืน” จำนวนงบประมาณทั้งสิ้น 88,480 บาท (แปดหมื่นแปดพันสี่ร้อยสี่สิบแปดบาทถ้วน) ไปแล้วนั้น', '', 'จึงเรียนมาเพื่อโปรดพิจารณาอนุมัติ', 'AAAAAAAAA', '02:01', 'สุนีนารถ พลซา', '15:11', '', '', 'อาจารย์ชัยศนันทน์ สมปัญญาธิวงศ์', 'ชัยศนันทน์ สมปัญญาธิวงศ์', '2', '', '', '', 'อารีย์ ลำมะยศ', ' ', '15:42', '', '', '', '', '', '', '', '', '', '', ''),
(2, 'สำนักงานคณะ', 'ผู้ปฏิบัติงานบริหาร', '10', '0002', '', '2 ม.ค. 2565', 'ขออนุญาตเปลี่ยนแปลงวันที่ดำเนินโครงการ', 'ตามบันทึกข้อความที่ 07775 ลงวันที่ 25 ตุลาคม 2564 มหาวิทยาลัยราชภัฏเลยได้อนุมัติดำเนิน โครงการธนาคารขยะชุมชน สร้างความมั่นคงให้ครอบครัว พัฒนาชุมชนอย่างยั่งยืน” จำนวนงบประมาณทั้งสิ้น 88,480 บาท (แปดหมื่นแปดพันสี่ร้อยสี่สิบแปดบาทถ้วน) ไปแล้วนั้น เนื่องด้วยในวันที่ 28 พฤศจิกายน 2564 สำนักงานคณะกรรมการการเลือกตั้ง (กกต.) กำหนดให้มีการเลือกตั้งสมาชิกสภาองค์การบริหารส่วนตำบลและนายกองค์การบริหารส่วนตำบล (อบต.) จึงทำให้กลุ่มชาวบ้านและวิทยากรที่จะเข้าร่วมกิจกรรมในวันที่ 28 พฤศจิกายน 2564 นั้นไม่สามารถมาเข้าร่วมกิจกรรมได้ ดังนั้นจึงขอเปลี่ยนแปลงวันที่ดำเนินโครงการธนาคารขยะชุมชน สร้างความมั่นคงให้ครอบครัว พัฒนาชุมชนอย่างยั่งยืน เป็นวันที่ 1 ธันวาคม 2564  ดังรายละเอียดที่แนบมาพร้อมนี้', '', 'จึงเรียนมาเพื่อโปรดพิจารณาอนุมัติ', 'สุภาพร เมืองจันทร์', '10:40', '', '', 'กุสุมา อินทรา', '10:42', 'นางสาวสุภาพร เมืองจันทร์', 'ผู้ปฏิบัติงานบริหาร', '5', '', '', '', '', '', '', 'มศ.001/64', '', '', '', 'บัวบาล', 'เห็นควรอนุญาต', '10:45', 'ดร.เยาวพา กองเกตุ', 'อนุญาต', '10:47', ''),
(3, 'ภาควิชาสังคมศาสตร์', 'ผู้ปฏิบัติงานบริหาร', '6', '0003', '', '2 ม.ค. 2565', 'ขอเรียนเชิญเป็นวิทยากร', 'ด้วยสาขาวิชาสาขาวิชาดิจิทัลอาร์ต ภาควิชามนุษยศาสตร์ คณะมนุษยศาสตร์และสังคมศาสตร์ มหาวิทยาลัยราชภัฏเลย ได้จัดทำโครงการย่อยที่ ๑“การอบรมเรื่องการเลี้ยงไส้เดือนดิน”  ภายใต้โครงการยุทธศาสตร์มหาวิทยาลัยราชภัฏเพื่อการพัฒนาท้องถิ่น มหาวิทยาลัยราชภัฏเลย   ประจำปีงบประมาณ  พ.ศ. ๒๕๖๕  โดยมีวัตถุประสงค์เพื่อส่งเสริมอาชีพการเลี้ยงไส้เดือนดินของชุมชน และเพื่อพัฒนาเศรษฐกิจระดับชุมชน ก่อให้เกิดเพิ่มรายได้เสริม และลดรายจ่ายให้กับคนในชุมชน  ', 'ในการนี้  สาขาวิชาสาขาวิชาดิจิทัลอาร์ต ภาควิชามนุษยศาสตร์ คณะมนุษยศาสตร์และสังคมศาสตร์ มหาวิทยาลัยราชภัฏเลย  ได้พิจารณาแล้วเห็นว่าท่านเป็นผู้มีความรู้  ความสามารถ  และประสบการณ์ในเรื่องดังกล่าว จึงขอเรียนเชิญท่านเป็นวิทยากรบรรยายในหัวข้อ  “ความรู้ทั่วไปเกี่ยวกับไส้เดือนดิน  การเลี้ยงไส้เดือนดิน การจัดการระบบการดูแลไส้เดือนดิน และการผลิตปุ๋ยมูลไส้เดือนดินและน้ำหมักมูลไส้เดือนดิน” ในวันเสาร์ที่  ๑๙  มีนาคม  ๒๕๖๕  ตั้งแต่เวลา  ๐๘.๐๐ – ๑๗.๐๐ น. ณ บ้านทุ่งใหญ่  ตำบลภูกระดึง อำเภอภูกระดึง จังหวัดเลย  รายละเอียดตามกำหนดการที่แนบมาพร้อมนี้', 'จึงเรียนมาเพื่อโปรดพิจารณาและขอขอบคุณมา  ณ  โอกาสนี้', 'มยุรี  ก้อนคำใหญ่', '11:40', '', '', 'กุสุมา', '15:11', 'นางสาวมยุรี ก้อนคำใหญ่', 'เจ้าหน้าที่', '4', '', '', '', 'วีระยุทธ', ' ', '15:04', 'มศ.005/64', '', '', '', 'บัวบาล', '', '15:16', '', '', '', ''),
(4, 'สำนักงานคณะ', 'นักวิชาการศึกษา', '8', '0004', '', '2 ม.ค. 2565', 'ขออนุญาตไปราชการ', 'ตามที่สาขาวิชายุทธศาสตร์การพัฒนาภูมิภาค', 'ในการนี้จึงขออนุญาตไปราชการ', 'จึงเรียนมาเพื่อโปรดพิจารณา', 'ผศ.ดร.พรกมล ระหาญนอก', '11:15', '', '', 'กุสุมา', '10:58', 'นางสาวปุยฝ้าย อเนกบุณย์', 'นักวิชาการศึกษา', '4', '', '', '', '', '', '', 'มศ.003/64', '', '', '', 'บัวบาล', '', '11:21', '', '', '', ''),
(5, 'ภาควิชามนุษยศาสตร์', 'สาขาวิชาดิจิทัลอาร์ต', '51', '0005', 'ภม.001/64', '2 ม.ค. 2565', 'ขอเสนอเปิดรายวิชาเลือกเสรี สำหรับนักศึกษาระดับปริญญาตรี ในปีการศึกษา ๓/๒๕๖๔', 'ด้วยสาขาวิชาดิจิตอลอาร์ต ภาควิชามนุษยศาสตร์ คณะมนุษยศาสตร์และสังคมศาสตร์  มหาวิทยาลัยราชภัฏเลย ข้าพเจ้าผู้ช่วยศาสตราจารย์ศรัณยพัชร์ จิตพิไล มีความประสงค์จะขอเปิดรายวิชาเลือกเสรี ๒๐๔๒๕๐๗ การถ่ายภาพแฟชั่น สำหรับนักศึกษาระดับปริญญาตรี ในปีการศึกษา ๒๕๖๔ เป็นต้นไป \r\nเพื่อให้การดำเนินการเปิดรายวิชาเป็นไปด้วยความเรียบร้อย โดยจะขอเปิดการเรียนการสอนในรายวิชาดังกล่าวตามตารางวิชาเลือกเสรี คือทุกวันอังคาร ในคาบที่ ๘ – ๑๑ ที่ห้องสตูดิโอภาพนิ่ง อาคารภาคศิลป์ คณะมนุษยศาสตร์และสังคมศาสตร์ และย้ายรายวิชา ๒๐๓๒๒๐๔ ภาพวาดคาแรคเตอร์การ์ตูนและการ์ตูนเล่าเรื่อง หมู่เรียน ศป.๖๒๐๓ ไปเป็นทุกวันศุกร์ ในคาบที่ ๑๑ – ๑๔ ที่ห้องคอม ๑๐๕ อาคารศูนย์คอมพิวเตอร์ฯ หรือตามห้องเรียนที่ว่าง รายละเอียดดังเอกสารแนบ\r\n', '', 'จึงเรียนมาเพื่อโปรดพิจารณา', 'ผู้ช่วยศาสตราจารย์ศรัณยพัชร์ จิตพิไล', '11:09', 'สุนีนารถ พลซา', '15:57', 'กุสุมา', '15:46', 'ผู้ช่วยศาสตราจารย์ศรัณยพัชร์ จิตพิไล', 'อาจารย์ผู้สอนในรายวิชาเลือกเสรี', '2', '', '', '', 'อารีย์ ลำมะยศ', ' ', '16:00', 'มศ.007/64', '', '', '', 'บัวบาล', 'ผ่าน', '11:08', 'ดร.เยาวพา กองเกตุ', 'อนุมัติ', '11:12', ''),
(6, 'สำนักงานคณะ', 'นักวิชาการศึกษา', '7', '0006', '', '2 ม.ค. 2565', 'ขออนุญาตไปราชการ', 'ตามที่คณะมนุษย์ศาสตร์และสังคมศาสตร์', 'ในการนี้ 12 เนื่องจาก….', 'จึงเรียนมาเพื่อโปรดพิจารณา', 'ปุยฝ้าย', '11:06', '', '', 'กุสุมา', '10:56', 'นางสาวศกุลตรา นามวงษ์', 'นักวิชาการศึกษา', '5', '', '', '', '', '', '', 'มศ.002/64', '', '', '', 'บัวบาล', '', '11:07', 'ดร.เยาวพา กองเกตุ', 'อนุมัติ', '11:12', ''),
(7, 'สำนักงานคณะ', 'เจ้าหน้าที่บริหารงานทั่วไป', '13', '0007', '', '2 ม.ค. 2565', 'ขออนุญาตไปราชการ', 'ตามที่คณะมนุษย์ศาสตร์และสังคมศาสตร์ ได้ดำเนินโครงการบริการวิชาการ …ในพื้นที่', 'จึงขออนุญาตไปราชการ ในวันที่ 3-4 ก.พ. 64  เพื่อบริการวิชาการในพื้นที่เป้าหมาย..', 'จึงเรียนมาเพื่อโปรดพิจารณาอนุญาต', 'นายทรัพย์อนันต์  พละบุตร', '11:31', '', '', 'กุสุมา', '15:46', 'นางวิไลภรณ์ สีสมพร', 'เจ้าหน้าที่บริหารงานทั่วไป', '4', '', '', '', '', '', '', 'มศ.004/64', '', '', '', 'บัวบาล', '', '15:48', '', '', '', ''),
(8, 'ภาควิชาสังคมศาสตร์', 'นักวิทยาศาสตร์', '11', '0008', '', '2 มี.ค. 2565', 'ขอรับการสนับสนุนครุภัณฑ์เพื่อใช้ในห้องสมุดสาขาวิชานิติศาสตร์  ศูนย์การศึกษามหาวิทยาลัย  ราชภัฏเลย  จังหวัดขอนแก่น ', 'ตามบันทึกข้อความสาขาวิชานิติศาสตร์ ภาควิชาสังคมศาสตร์ คณะมนุษยศาสตร์และสังคมศาสตร์ ที่ มส.๑๘๔๓/๒๕๖๔ เรื่อง ขออนุญาตใช้ห้องเรียน CL๓๐๑-CL๓๐๔ อาคารเรียนรวม CL๐๑    เพื่อเป็นการเตรียมความพร้อมจัดสรรพื้นที่สำหรับใช้ในการจัดการเรียนการสอน  และในการรองรับการประเมินคุณภาพจากองค์กรวิชาชีพ ณ ศูนย์การศึกษามหาวิทยาลัยราชภัฎเลย จังหวัดขอนแก่น ทั้งในภาคปกติและภาคพิเศษ ไปแล้วนั้น ', 'เพื่อให้การจัดการเรียนการสอนของสาขาวิชานิติศาสตร์เกิดประสิทธิภาพสูงสุด         สาขาวิชานิติศาสตร์จึงขอรับการสนับสนุนครุภัณฑ์จากสำนักวิทยบริการและเทคโนโลยีสารสนเทศ เพื่อใช้ในห้องสมุดสาขาวิชานิติศาสตร์ ณ ศูนย์การศึกษามหาวิทยาราชภัฏเลย  จังหวัดขอนแก่น  ดังนี้ \r\n๑. โต๊ะอ่านหนังสือเดี่ยวพร้อมเก้าอี้ ๑๐ ชุด\r\n๒. โต๊ะอ่านหนังสือ ๘ ตัว พร้อมเก้าอี้ ๑๖ ตัว\r\n๓. ตู้ชั้นหนังสือ ๖ ตู้ \r\nรายละเอียดปรากฎตามเอกสารที่แนบมาพร้อมนี้    \r\n\r\n', 'จึงเรียนมาเพื่อโปรดทราบและพิจารณา \r\n', 'นางสาวปิยวรรณ  เสนาเจริญ', '10:43', '', '', 'กุสุมา', '11:00', 'นางสาววิภาพร สวัสดี', 'ผู้ปฏิบัติงาน', '', '1', '', '', '', '', '', 'มศ.006/64', '', '', '', '', '', '', '', '', '', ''),
(9, 'ภาควิชาสังคมศาสตร์', 'บรรณารักษ์', '12', '0009', '', '2 มี.ค. 2565', 'ขอให้ลงนามในหนังสือขอขอบคุณ', 'ด้วยสาขาวิชานิติศาสตร์ได้รับมอบวารสารวิชาการ จากคณะนิติศาสตร์ มหาวิทยาลัยราชภัฏอุบลราชธานี และคู่มือล่ามในกระบวนการยุติธรรมและแบบรายงานผลดำเนินการใช้บัญชีล่ามในกระบวนการยุติธรรมของกรมคุ้มครองสิทธิและเสรีภาพ จากกรมคุ้มครองสิทธิและเสรีภาพ นั้น', 'เพื่อให้การดำเนินการเรื่องดังกล่าวเป็นไปด้วยความเรียบร้อย จึงขอให้ท่านลงนามในหนังสือขอขอบคุณ  เพื่อสาขาวิชาจะได้ดำเนินการต่อไป', ' จึงเรียนมาเพื่อโปรดพิจารณา', 'นางสาวปิยวรรณ  เสนาเจริญ', '10:50', '', '', 'กุสุมา', '12:09', 'นางสาวศิริลักษณ์ ราชพัฒน์', 'ประธานกรรมการผู้รับผิดชอบหลักสูตรนิติศาสตรบัณฑิต สาขาวิชานิติศาสตร์', '4', '', '', '', '', '', '', 'มศ.009/64', '', '', '', '[บัวบาล', '', '14:24', '', '', '', ''),
(10, 'สำนักงานคณะ', 'นักวิชาการเงินและบัญชี', '5', '0010', '', '2 ม.ค. 2565', 'ขอเชิญประชุมเรื่องจัดสรรงบประมาณปี2565', 'ด้วยมหาวิทยาลัยได้จัดสรรงบประมาณประจำปี2565', 'จึงจอเขิญประชุม', 'เรียนมาโปรดเข้าร่วมประชุม', 'ดร.เยาวพา กองเกตุ', '11:05', '', '', 'กุสุมา', '11:08', 'นางสาวจิรวรรณ บุตรเต', 'นักวิชาการเงินและบัญชี', '', '1', '', '', '', '', '', 'มศ.008/64', '', '', '', '', '', '', '', '', '', ''),
(11, 'ภาควิชามนุษยศาสตร์', 'ภาควิชามนุษยศาสตร์', '3', '0011', 'ภม.002/64', '2 มี.ค. 2565', 'ขอขยายเวลาส่งร่างงานวิจัยฉบับสมบูรณ์', 'ด้วยข้าพเจ้า นายธีรวัฒน์  วงศ์วรัญญู อาจารย์ประจำหลักสูตรรัฐประศาสนศาสตร์  ภาควิชาสังคมศาสตร์  คณะมนุษยศาสตร์และสังคมศาสตร์  ได้รับทุนอุดหนุนการวิจัยงบประมาณปีพ.ศ. 2563  เรื่อง  “กระบวนการรับรู้นโยบายสาธารณะของประชาชนในจังหวัดเลย” งบประมาณ  40,000 (สี่หมื่นบาทถ้วน)  โดยสถาบันวิจัยและพัฒนากำหนดให้ส่ง (ร่าง)  รายงานฉบับสมบูรณ์  ภายในวันที่  20  มกราคม  2565  นั้น', 'ดังนั้น ข้าพเจ้าฯ มีความจำเป็นต้องขอขยายระยะเวลาในการส่งร่างรายงานฉบับสมบูรณ์ ตามข้างต้น  เนื่องจากเกิดความล่าช้าในการลงพื้นที่เก็บข้อมูลวิจัย  ข้าพเจ้าฯ จึงขออนุญาตขยายระยะเวลา   ในการส่งรายงานสมบูรณ์ออกไปเป็นระยะเวลา 3 เดือนทั้งนี้ตั้งแต่  วันที่  21  มกราคม  2565  ถึงวันที่   21  เมษายน  2565', 'จึงเรียนมาเพื่อโปรดพิจารณา', 'สุนีนารถ', '11:12', 'สุนีนารถ พลซา', '15:55', 'กุสุมา', '17:15', 'นางสาวสุนีนารถ พลซา', 'เจ้าหน้าที่', '3', '', '', '', 'อารีย์ ลำมะยศ', ' ', '15:59', '��ศ.0023/64', '', '', '', 'บัวบาล', '', '11:27', '', '', '', ''),
